func TestAddHostNameToUserData(t *testing.T) {
	userData := "{\"ignition\":{\"config\":{\"merge\":[{\"source\":\"https://192.168.123.15:22623/config/worker\"}]},\"security\":{\"tls\":{\"certificateAuthorities\":[{\"source\":\"data:text/plain;charset=utf-8;base64,\"}]}},\"version\":\"3.1.0\"}}"

	expected := "{\"ignition\":{\"config\":{\"merge\":[{\"source\":\"https://192.168.123.15:22623/config/worker\"}]},\"security\":{\"tls\":{\"certificateAuthorities\":[{\"source\":\"data:text/plain;charset=utf-8;base64,\"}]}},\"version\":\"3.1.0\"},\"storage\":{\"files\":[{\"contents\":{\"source\":\"data:,testHostName\"},\"filesystem\":\"root\",\"mode\":420,\"path\":\"/etc/hostname\"}]}}"

	result, _ := addHostnameToUserData([]byte(userData), "testHostName")
	assert.Equal(t, string(result), expected)

}
